// This function will process the design assets and generate DALL-E prompts
// for each mockup description

const designAssets = $input.first().json.design_assets
const mockupPrompts = [];

// Extract all DALL-E prompts from the design assets
for (const asset of designAssets) {
  const conceptTitle = asset.concept_title;

  for (const mockup of asset.mockup_descriptions) {
    mockupPrompts.push({
      concept: conceptTitle,
      channel: mockup.channel,
      prompt: mockup.gemini_prompt
    });
  }
}

return {
  mockup_prompts: mockupPrompts,
  design_assets: designAssets
};

